def avgVect(a,b,c,d,e):
    avg = []
    for x in range(0, 100):
        avg.append((a[x][-1:][0] + b[x][-1:][0] + c[x][-1:][0] + d[x][-1:][0] + e[x][-1:][0]) / 5)
    return avg


